import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/flare.json')
  .then(res => res.json())
  .then(data => {
    const dv = new DataSet.View().source(data, {
      type: 'hierarchy'
    });
    dv.transform({
      type: 'hierarchy.cluster'
    });

    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
      padding: [60, 0, 40, 0]
    });
    chart.axis(false);
    chart.legend(false);
    chart.coordinate('polar');
    chart.scale({
      x: {
        nice: true,
        sync: true,
      },
      y: {
        nice: true,
        sync: true,
      },
    });

    const edgeView = chart.createView();
    edgeView.data(dv.getAllLinks().map(link => ({
      x: [link.source.x, link.target.x],
      y: [link.source.y, link.target.y],
      source: link.source.id,
      target: link.target.id
    })));
    edgeView.edge()
      .position('x*y')
      .shape('smooth') // vhv
      .color('grey')
      .tooltip('source*target')
      .style({
        strokeOpacity: 0.5,
      });

    const nodeView = chart.createView();
    nodeView.data(dv.getAllNodes().map(node => ({
      hasChildren: !!(node.data.children && node.data.children.length),
      name: node.data.name,
      value: node.value,
      depth: node.depth,
      x: node.x,
      y: node.y
    })));
    nodeView.point()
      .position('x*y')
      .color('hasChildren')
      .label('name', {
        offset: 5,
        labelEmit: true,
        style:  {
          fill: 'grey',
          fontSize: 9,
        }
      });

    chart.render();
  });

