        x: [link.source.x, link.target.x],
        y: [link.source.y, link.target.y],
        source: link.source.id,
        target: link.target.id
      };
    }));
    edgeView.edge().position('x*y').shape('smooth') // vhv
    .color('grey').tooltip('source*target').style({
      strokeOpacity: 0.5
    });
    var nodeView = chart.createView();
    nodeView.data(dv.getAllNodes().map(function (node) {
      return {
        hasChildren: !!(node.data.children && node.data.children.length),
        name: node.data.name,
        value: node.value,
        depth: node.depth,
        x: node.x,
        y: node.y
      };
    }));
    nodeView.point().position('x*y').color('hasChildren').label('name', {
      offset: 0,
      labelEmit: true,
      style: {
        fill: 'grey',
        fontSize: 9
      }
    });
    chart.render();
  });
});

allDemos.source:
import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/sunburst.json')
  .then((res) => res.json())
  .then((data) => {
    const { DataView } = DataSet;
    const dv = new DataView();
    dv.source(data, {
      type: 'hierarchy',
    }).transform({
      type: 'hierarchy.partition', // 根据树形数据生成相邻层次图 Adjacency Diagram 布局
      field: 'sum',
      as: ['x', 'y'],
    });
    const source = [];
    const nodes = dv.getAllNodes();
    nodes.map((node) => {
      if (node.depth === 0) {
        // 父节点不展示
        return null;
      }
      const obj = {};
      obj.label = node.data.label;
      obj.sum = node.data.sum;
      obj.uv = node.data.uv;
      obj.value = node.value;
      obj.x = node.x;
      obj.y = node.y;
      source.push(obj);
      return node;
    });
    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
    });
    chart.data(source);
    chart.scale({
      x: {
        nice: true,
      },
      y: {
        nice: true,
      },
    });

    chart.coordinate('polar', {
      innerRadius: 0.3,
    });
    chart.tooltip({
      showTitle: false,
      showMarkers: false,
    });
    chart.axis(false);
    chart.legend(false);

    chart
      .polygon()
      .position('x*y')
      .color('value', '#BAE7FF-#1890FF-#0050B3')
      .style({
        stroke: '#FFF',
        lineWidth: 1,
      })
      .tooltip('label*sum');

    chart.interaction('element-active');

    chart.render();
  });

