import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/diamond.json')
  .then((res) => res.json())
  .then((data) => {
    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
    });

    chart.data(data);
    chart.scale({
      carat: {
        alias: '克拉数',
        min: 0,
        max: 4,
        sync: true,
      },
      price: {
        alias: '价格',
        sync: true,
        nice: true,
      },
    });

