  [6, 2, 88],
  [6, 3, 98],
  [6, 4, 96],
  [7, 0, 31],
  [7, 1, 1],
  [7, 2, 82],
  [7, 3, 32],
  [7, 4, 30],
  [8, 0, 85],
  [8, 1, 97],
  [8, 2, 123],
  [8, 3, 64],
  [8, 4, 84],
  [9, 0, 47],
  [9, 1, 114],
  [9, 2, 31],
  [9, 3, 48],
  [9, 4, 91],
];

const source = data.map((arr) => {
  return {
    name: arr[0],
    day: arr[1],
    sales: arr[2],
  };
});
const chart = new Chart({
  container: 'container',
  autoFit: true,
  height: 500,
});

chart.data(source);

chart.scale('name', {
  type: 'cat',
  values: ['Alexander', 'Marie', 'Maximilian', 'Sophia', 'Lukas', 'Maria', 'Leon', 'Anna', 'Tim', 'Laura'],
});
chart.scale('day', {
  type: 'cat',
  values: ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday'],
});
chart.scale('sales', {
  nice: true,
});

chart.axis('name', {
  tickLine: null,
  grid: {
    alignTick: false,
    line: {
      style: {
        lineWidth: 1,
        lineDash: null,
        stroke: '#f0f0f0',
      },
    },
  },
});

chart.axis('day', {
  title: null,
  grid: {
    alignTick: false,
    line: {
      style: {
        lineWidth: 1,
        lineDash: null,
        stroke: '#f0f0f0',
      },
    },
  },
});

chart.tooltip(false);
chart.interaction('brush-visible');

chart
  .polygon()
  .position('name*day')
  .color('sales', '#BAE7FF-#1890FF-#0050B3')
  .label('sales', {
    offset: -2,
    style: {
      fill: '#fff',
      shadowBlur: 2,
      shadowColor: 'rgba(0, 0, 0, .45)',
    },
  })
  .style({
    lineWidth: 1,
    stroke: '#fff',
  }).state({
    active: {
      style: {
        fillOpacity: 0.9
      },
      animate: false
    },
    inactive: {
      style: {
        fillOpacity: 0.4
      },
      animate: false
    }
  });;

chart.render();

