    chart.data(data);
    chart.scale({
      price: { nice: true },
      carat: { nice: true },
      density: { nice: true },
    });

    chart.point()
      .position('carat*price');

    const view = chart.createView({
      padding: 0,
    });
    view.axis(false);
    view.data(dv.rows);
    view.heatmap()
      .position('carat*price')
      .color('density', 'blue-cyan-lime-yellow-red');

    chart.render();
  });

