import DataSet from '@antv/data-set';
import { Chart } from '@antv/g2';

function getData(data) {
  const tmp = [];
  const dates = [];
  data.male.values.forEach(function (obj) {
    if (dates.indexOf(obj.date) === -1) {
      dates.push(obj.date);
    }
    obj.age_groups.forEach(function (subObject) {
      subObject.gender = 'male';
      subObject.date = obj.date;
      tmp.push(subObject);
    });
  });
  data.female.values.forEach(function (obj) {
    obj.age_groups.forEach(function (subObject) {
      subObject.gender = 'female';
      subObject.date = obj.date;
      tmp.push(subObject);
    });
  });

  const ds = new DataSet();
  const dv = ds.createView()
    .source(tmp)
    .transform({
      type: 'filter',
      callback(row) { // 判断某一行是否保留，默认返回true
        return new Date(row.date * 1000).getFullYear() === new Date(dates[0] * 1000).getFullYear();
      }
    });

  return dv.rows;
}

const axisHeight = 50;

fetch('https://gw.alipayobjects.com/os/antvdemo/assets/data/population.json')
  .then(res => res.json())
  .then(data => {
    const chart = new Chart({
      container: 'container',
      autoFit: true,
      height: 500,
      padding: [0, 48, 0, 48]
    });

    chart.data(getData(data));
    chart.scale({
      age: {
        sync: true,
        tickCount: 11
      },
      total_percentage: {
        sync: true,
        formatter(v) {
          return v + '%';
        }
      },
      gender: {
        sync: true
      }
    });

    chart.facet('mirror', {
      fields: ['gender'],
      transpose: false,
      padding: [axisHeight / 2, 0, axisHeight / 2, 0],
      eachView(view) {
        view.interval()
          .position('age*total_percentage')
          .color('gender', ['#1890ff', '#f04864']);
      }
    });

    chart.on('afterrender', () => {
      const view = chart.views[0];
      const { height: rHeight } = chart.viewBBox;
      const { height: vHeight } = view.viewBBox
      view.axis('age', {
        position: 'bottom',
        label: {
          style: {
            textAlign: 'center',
            textBaseline: 'middle'
          },
          offset: rHeight / 2 - vHeight + axisHeight / 2
        },
        tickLine: false
      })
      view.render()
    })

    chart.render();
  });

