  .polygon()
  .position('x*y') // nodes数据的x、y由layout方法计算得出
  .color('name')
  .label('x*name', (x, name) => {
    const isLast = x[1] === 1;
    return {
      style: {
        fill: '#545454',
        textAlign: isLast ? 'end' : 'start',
      },
      offsetX: isLast ? -8 : 8,
      content: name,
    };
  })
  .tooltip(false)
  .style('name', (name) => {
    if (name === '跳失') {
      return {
        fill: '#FF6010',
        stroke: '#FF6010',
      };
    }
    if (name === '首页 UV') {
      return {
        fill: '#5D7092',
        stroke: '#5D7092',
      };
    }
    return {};
	});

// edge view
