  var view1 = chart.createView({
    region: {
      start: {
        x: 0,
        y: 0
      },
      end: {
        x: 0.5,
        y: 1
      }
    }
  });
  view1.coordinate('theta', {
    radius: 0.7,
    startAngle: 0 + otherOffsetAngle,
    endAngle: Math.PI * 2 + otherOffsetAngle
  });
  view1.data(data);
  view1.interaction('element-highlight');
  view1.interval().adjust('stack').position('value').color('type', ['#38c060', '#2593fc']).label('value', function () {
    return {
      offset: -10,
      content: function content(obj) {
        return obj.type + '\n' + obj.value + '%';
      }
    };
  });
  var view2 = chart.createView({
    region: {
      start: {
        x: 0.5,
        y: 0.1
      },
      end: {
        x: 1,
        y: 0.9
      }
    }
  });
  view2.axis(false);
  view2.data(other);
  view2.interaction('element-highlight');
  view2.interval().adjust('stack').position('value').color('type', ['#063d8a', '#0b53b0', '#1770d6', '#2593fc', '#47abfc', '#6dc1fc', '#94d6fd', '#bbe7fe']).label('value', {
    position: 'right',
    offsetX: 5,
    offsetY: 10,
    content: function content(obj) {
      return obj.type + ' ' + obj.value + '%';
    }
  });
  chart.render();
  drawLinkArea();
  chart.on('afterpaint', function () {
    drawLinkArea();
  });
  /* ---------绘制连接区间-----------*/

  function drawLinkArea() {
    var canvas = chart.getCanvas();
    var container = chart.backgroundGroup;
    var view1_coord = view1.getCoordinate();
    var center = view1_coord.getCenter();
    var radius = view1_coord.getRadius();
    var interval_geom = view2.geometries[0];
    var interval_container = interval_geom.container;
    var interval_bbox = interval_container.getBBox();
    var view2_coord = view2.getCoordinate(); // area points

    var pie_start1 = {
      x: center.x + Math.cos(Math.PI * 2 - otherOffsetAngle) * radius,
      y: center.y + Math.sin(Math.PI * 2 - otherOffsetAngle) * radius
    };
    var pie_start2 = {
      x: center.x + Math.cos(otherOffsetAngle) * radius,
      y: center.y + Math.sin(otherOffsetAngle) * radius
    };
    var interval_end1 = {
      x: interval_bbox.minX,
      y: view2_coord.end.y
    };
    var interval_end2 = {
      x: interval_bbox.minX,
      y: view2_coord.start.y
    };
    var path = [['M', pie_start1.x, pie_start1.y], ['L', pie_start2.x, pie_start2.y], ['L', interval_end2.x, interval_end2.y], ['L', interval_end1.x, interval_end1.y], ['Z']];
    container.addShape('path', {
      attrs: {
        path: path,
        fill: '#e9f4fe'
      }
    });
    canvas.draw();
  }
});

