];

const chart = new Chart({
  container: 'container',
  autoFit: true,
  height: 500,
  padding: [20, 20, 20, 70]
});

chart.data(data);
chart.legend(false);
chart.tooltip({
  showMarkers: false
});
chart.facet('rect', {
  fields: ['class'],
  columnTitle: {
    offsetY: -15,
    style: {
      fontSize: 14,
      fontWeight: 300,
      fill: '#8d8d8d'
    }
  },
  eachView: (view, facet) => {
    view.coordinate().transpose();

    if (facet.columnIndex === 0) {
      view.axis('country', {
        tickLine: null,
        line: null,
      });
      view.axis('value', false);
    } else {
      view.axis(false);
    }
    const color = getTypeColor(facet.columnValue);
    view
      .interval()
      .adjust('stack')
      .position('country*value')
      .color('type', [color, '#ebedf0'])
      .size(20)
      .label('value*type', (value, type) => {
        if (type === '2') {
          return null;
        }
        const offset = (value < 30) ? 10 : -4;
        return {
          offset,
        };
      });
    view.interaction('element-active');
  }
});
chart.render();

