          }
        }); // 配置 tooltip

        chart.tooltip({
          showMarkers: false,
          title: 'country'
        }); // 配置图例

        chart.legend('population', false);
        chart.legend('continent', {
          flipPage: false,
          position: 'bottom-left'
        }); // 坐标轴配置

        chart.axis('life', {
          title: {
            style: {
              fill: '#8C8C8C',
              fontSize: 14
            }
          },
          line: {
            style: {
              stroke: '#D9D9D9'
            }
          }
        });
        chart.axis('income', {
          title: {
            style: {
              fill: '#8C8C8C',
              fontSize: 14
            }
          },
          grid: {
            line: {
              style: {
                stroke: '#D9D9D9'
              }
            }
          }
        }); // 绘制散点图

        chart.point().position('income*life').color('continent', function (val) {
          return colorsMap[val];
        }).size('population', [1, 25]).shape('circle').animate({
          update: {
            duration: 200,
            easing: 'easeLinear'
          }
        }).tooltip('life*income*population').style({
          stroke: '#000'
        }); // 绘制标注文本

        chart.annotation().text({
          position: ['50%', '50%'],
          content: year,
          style: {
            fontSize: 200,
            fill: '#999',
            textAlign: 'center',
            fillOpacity: 0.3
          },
          top: false,
          animate: false
        });
        chart.render();
      } else if (count < years.length) {
        chart.annotation().clear(true);
        chart.annotation().text({
          position: ['50%', '50%'],
          content: year,
          style: {
            fontSize: 200,
            fill: '#999',
            textAlign: 'center',
            fillOpacity: 0.3
          },
          top: false,
          animate: false
        });
        chart.changeData(data[year]);
      }

      ++count;

      if (count === years.length) {
        clearInterval(interval);
      }
    }

    countUp();
    interval = setInterval(countUp, 200);
  });
});

